import React from 'react'
import photo from '../multiview/posters/12_Angry_men.jpg'
import 'react-dropzone-uploader/dist/styles.css'
import Dropzone from 'react-dropzone-uploader'
import { FaFileUpload } from 'react-icons/fa' 

const PosterUpload = () => {
    const [file, setFile] = React.useState(null)
    const [isfile, setIsFile] = React.useState(false)

    const fileSelectedHandler = e => {
        setFile(URL.createObjectURL(e.target.files[0]))
        setIsFile(true)
    }

    const handleClick = e => {
        setFile(e.target.files[0])
    }

    return(
        <div style={parent_test}>
            <div style={child_1}>
                {isfile
                    ? <img src={file} alt="Testing" style={testk}/>
                    : <React.Fragment>
                        <p>Balls</p>
                        <FaFileUpload />
                      </React.Fragment>
                }
            </div>
            <div style={child_2}>
                <input type="file" onChange={fileSelectedHandler} id="unique_input"/>
                <label for="unique_input">Upload Poster</label>
            </div>
        </div>
    );
}

//<img src={photo} alt="Please Upload an Image" style={poster_picture}/>
//<button style={button_style}>Upload</button>

const parent_test = {
    width: "80%",
    height: "100%",
    display: "flex",
    flexDirection: "column"
}

const child_1 = {
    width: "300px",
    flexGrow: "9",
    backgroundColor: "gray",
    display: "flex"
}

const child_2 = {
    width: "300px",
    flexGrow: "1",
    backgroundColor: "green",
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
}


const button_style = {
    height: "100%",
    width: "80%",
    fontSize: "20pt"
}

const testk = {
    flexGrow: 1
}
export default PosterUpload
