import React from 'react'
import Navbar from '../components/navbar/navbar'
import Sidebar from '../components/sidebar/sidebar'

const sidebar_options = {
    marginTop: {
        marginTop: "2vh"
    },
    blank: false,
    blocks: {
        item1: {
            title: "Add Movie",
            link: "/createMovie"
        },
        item2: {
            title: "List View",
            link: "/listview"
        },
        item3: {
            title: "Multi View",
            link: "/multiview"
        }
    }
}

const UserPage = () => {
    return(
        <div className="single_page_background">
            <Navbar />
            <Sidebar input={sidebar_options}/>
            <div style={page}>
                <div style={card}>
                    <p style={title}>Welcome to the Movie Database</p>
                    <p style={row}>Your Username Is:</p>
                    <p style={row}>{localStorage.getItem("username")}</p>
                    <p style={row}>Your User ID Is:</p>
                    <p style={row}>{localStorage.getItem("userId")}</p>
                    <p style={row}>Logout</p>
                    <button>Logout</button>
                </div>
            </div>
        </div>
    );
}


const row = {
    flexGrow: "2",
    minHeight: "40px",
    paddingLeft: "2%",
    fontSize: "15pt"
}

const title = {
    fontSize: "35pt",
    fontWeight: "bold",
    flexGrow: "4"
}

const card = {
    width: "60%",
    height: "80%",
    backgroundColor: "#EEEEEE",
    borderRadius: "20px"
}

const page = {
    width: "85%",
    height: "100%",
    float: "right",
    padding: "5px"
}

export default UserPage
