import React from 'react';
import { FaFileUpload } from 'react-icons/fa'

const Dropzone = (props) => {
    const [inDropzone, setinDropzone] = React.useState(false)
    //Display thumbnail photo upon upload
    const handleFiles = (files) => {
        console.log(files)
    }

    //Handle Drop of photo into Poster Upload Section
    const handleDrop = e => {
        e.preventDefault()
        e.stopPropagation()

        const dt = e.dataTransfer;
        const file = dt.files;

        this.handleFiles(file);
    }

    return (
        <div style={div_style}>
          {inDropzone
              ? <React.Fragment>
                  <div style={on}>
                  </div>
                </React.Fragment>
              : <React.Fragment>
                  <div style={off}>
                      <div style={upload_text_div}>
                          <p style={upload_text}>Drag Files to Upload</p>
                          <FaFileUpload />
                      </div>
                  </div>
                </React.Fragment>
          }
        </div>
      )

}

const upload_text = {
    fontSize: "35pt"
}

const upload_text_div = {
    width: "100%",
    height: "80%",
    marginTop: "10%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column"
}

const div_style = {
    width: "100%",
    height: "100%"
}

const on = {
    width: "100%",
    height: "100%",
    border: 'dashed grey 4px',
    backgroundColor: 'rgba(255,255,255,.8)',
    opacity: "0.3"
}

const off = {
    width: "100%",
    height: "100%",
    fontSize: "36pt",
    textAlign: "center"
}

export default Dropzone
