import React from 'react'
import SidebarElement from './sidebarElement'

const Sidebar = (props) => {

    const accessLevel = "admin"
    console.log('access level' + accessLevel)

    if(accessLevel == 'admin'){
        var sidebar_links_admin = [
            {
                index: 1,
                heading: "Add New Movie",
                title: "Add Movie",
                link: "/createMovie"
            },
            {
                index: 2,
                heading: "View Multiview",
                title: "Multiview",
                link: "/multiview"
            },
            {
                index: 3,
                heading: "View Listview",
                title: "Listview",
                link: "/listview"
            },
            {
                index: 4,
                heading: "View Singleview",
                title: "Singleview",
                link: "/singleview"
            }
        ]
        setLinks(sidebar_links_admin)
    }
    if(accessLevel == 'user'){
        var sidebar_links_user = [
            {
                index: 1,
                heading: "View Multiview",
                title: "Multiview",
                link: "/multiview"
            },
            {
                index: 2,  
                heading: "View Listview",
                title: "Listview",
                link: "/listview"
            },
            {
                index: 3,
                heading: "View Singleview",
                title: "Singleview",
                link: "/singleview"
            }
        ]
        setLinks(sidebar_links_user)
    }
    const temp = props;
    console.log(props)

    return(
        <div className="sidebar_div">
            {
                props.input.map((value, index) => {
                    return <SidebarElement input={value} />
                })
            }
        </div>
    );
}

const test = {
    color: "white",
    textAlign: "center"
}

export default Sidebar
