import React from 'react';
import { FaFileUpload } from 'react-icons/fa'

const Dropzone = () => {
    const reducer = (state, action) => {
        switch (action.type) {
          case 'SET_DROP_DEPTH':
            return { ...state, dropDepth: action.dropDepth }
          case 'SET_IN_DROP_ZONE':
            return { ...state, inDropZone: action.inDropZone };
          case 'ADD_FILE_TO_LIST':
            return { ...state, fileList: state.fileList.concat(action.files) };
          default:
            return state;
        }
      };
      const [data, dispatch] = React.useReducer(
        reducer, { dropDepth: 0, inDropZone: false, fileList: [] }
      )

    const handleDrag = e => {
        e.preventDefault()
        e.stopPropagation() 
    }

    const handleDragIn = e => {
        e.preventDefault()
        e.stopPropagation()
        this.dragCounter++
        if(e.dataTransfer.items && e.dataTransfer.items.length > 0){
            this.setState({drag: true})
        }
    }

    const handleDragOut = e => {
        e.preventDefault()
        e.stopPropagation()
        this.dragCounter--
        if (this.dragCounter === 0) {
            this.setState({drag: false})
          }
    }

    //Display thumbnail photo upon upload
    const handleFiles = (files) => {
        console.log(files)
    }

    //Handle Drop of photo into Poster Upload Section
    const handleDrop = e => {
        e.preventDefault()
        e.stopPropagation()

        const dt = e.dataTransfer;
        const file = dt.files;

        this.handleFiles(file);
    }

    return (
        <div style={div_style}>
          {this.state.drag
              ? <React.Fragment>
                  <div style={on}>
                  </div>
                </React.Fragment>
              : <React.Fragment>
                  <div style={off}>
                      <div style={upload_text_div}>
                          <p style={upload_text}>Drag Files to Upload</p>
                          <FaFileUpload />
                      </div>
                  </div>
                </React.Fragment>
          }
          {this.props.children}
        </div>
      )

}

const upload_text = {
    fontSize: "35pt"
}

const upload_text_div = {
    width: "100%",
    height: "80%",
    marginTop: "10%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column"
}

const div_style = {
    width: "100%",
    height: "100%"
}

const on = {
    width: "100%",
    height: "100%",
    border: 'dashed grey 4px',
    backgroundColor: 'rgba(255,255,255,.8)',
    opacity: "0.3"
}

const off = {
    width: "100%",
    height: "100%",
    fontSize: "36pt",
    textAlign: "center"
}

export default Dropzone
