import React from 'react'
import MoviePoster from '../multiview/movieposter/movieposter';
import angry_men from '../multiview/posters/12_Angry_men.jpg'
import dark_knight from '../multiview/posters/The_Dark_Knight.jpg'
import godfather from '../multiview/posters/Godfather.jpg'
import godfather2 from '../multiview/posters/Godfather_II.jpg'
import shawshank from '../multiview/posters/Shawshank_Redemption.jpg'
import ListElement from './listviewElement'

const ListView = () => {
    const [block1, setBlock1] = React.useState('')
    const [block2, setBlock2] = React.useState('')
    const [block3, setBlock3] = React.useState('')
    const [block4, setBlock4] = React.useState('')
    const [block5, setBlock5] = React.useState('')
    const [block6, setBlock6] = React.useState('')
    const [block7, setBlock7] = React.useState('')
    const [block8, setBlock8] = React.useState('')
    const [block9, setBlock9] = React.useState('')
    const [block10, setBlock10] = React.useState('')


    const [items, setItems] = React.useState({
        item1: {
            title: "Shawshank Redemption",
            rating: "9.3"
        },
        item2: {
            title: "The Godfather",
            rating: "9.1"
        },
        item3: {
            title: "The Godfather Part II",
            rating: "9.0"
        },
        item4: {
            title: "The Dark Knight",
            rating: "9.0"
        },
        item5: {
            title: "12 Angry Men",
            rating: "8.9"
        },
        item6: {
            title: "Lord of the Rings, Return of the King",
            rating: "8.9"
        },
        item7: {
            title: "Schindlers List",
            rating: "8.9"
        },
        item8: {
            title: "Pulp Fiction",
            rating: "8.8"
        },
        item9: {
            title: "The Good, The Bad and The Ugly",
            rating: "8.8"
        },
        item10: {
            title: "Lord of the Rings, Fellowship of the Ring",
            rating: "8.8"
        }
    })
    return(
        <div className="listview_style">
            <div className="listview_top_buffer"></div>
            <div className="listview_area">

            <ListElement picture={shawshank} input={items.item1}/>
            <ListElement picture={godfather} input={items.item2}/>
            <ListElement picture={godfather2} input={items.item3}/>
            <ListElement picture={dark_knight} input={items.item4}/>
            <ListElement picture={angry_men} input={items.item5}/>

            </div>
        </div>
    );
}


export default ListView
