import React from 'react'
import { FaFileUpload } from 'react-icons/fa' 

const AddMovieInput = (props) => {
    return(
        <div>
        <div style={div_left}>
            <div style={parent_test}>
                <div style={child_1}>
                    {props.isfile
                        ? <img src={props.file} alt="Testing" style={testk}/>
                        : <div>
                            <p style={testp}>Click Below to Upload</p>
                            <FaFileUpload style={testl}/>
                        </div>
                    }
                </div>
                <div style={child_2}>
                    <input type="file" onChange={props.fileSelectedHandler} id="unique_input" name="unique"/>
                    <label htmlFor="unique_input">Upload Poster</label>
                </div>
            </div>
        </div>
        <div style={div_right}>

        </div>
        </div>
    );
}

//Poster Upload
const testp = {
    fontSize: "20pt",
    textAlign: "center"
}
const parent_test = {
    width: "80%",
    height: "100%",
    display: "flex",
    flexDirection: "column"
}

const child_1 = {
    width: "300px",
    flexGrow: "9",
    backgroundColor: "gray",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center"
}

const child_2 = {
    width: "300px",
    flexGrow: "1",
    backgroundColor: "#57A0D3",
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
}

const testl = {
    width: "50px",
    height: "50px",
    margin: "0 125px 0 125px"
}

const button_style = {
    height: "100%",
    width: "80%",
    fontSize: "20pt"
}

const testk = {
    width: "100%",
    flexGrow: "1"
}


//Other
const div_left = {
    width: "300px",
    height: "90%",
    marginTop: "5%",
    float: "left",
    backgroundColor: "gray",
    display: "flex",
    flexDirection: "column",
    marginRight: "100px"
}

const div_right = {
    flexGrow: "2",
    height: "100%"
}
export default AddMovieInput
