import React, { useEffect } from 'react'
import Navbar from '../components/navbar/navbar'
import Sidebar from '../components/sidebar/sidebar'
import Singleview from '../components/singleview/singleview'
import axios from 'axios'
import { MdSettingsEthernet } from 'react-icons/md'

const sidebar_options = {
    marginTop: {
        marginTop: "2vh"
    },
    blank: false,
    blocks: {
        item1: {
            title: "Add Movie",
            link: "/createMovie"
        },
        item2: {
            title: "List View",
            link: "/listview"
        },
        item3: {
            title: "Multi View",
            link: "/multiview"
        },
        item4: {
            title: "Single View",
            link: "/singleview"
        }
    }
}

const SingleView = (props) => {
    const [title, setTitle] = React.useState('')
    const [poster, setPoster] = React.useState('')
    const [year, setYear] = React.useState('')
    const [synopsis, setSynopsis] = React.useState('')
    const [runtime, setRuntime] = React.useState('')
    const [rating, setRating] = React.useState('')
    const [director, setDirector] = React.useState('')
    const [genre, setGenre] = React.useState('')

    console.log(props.location.query)


    useEffect(() => {
        const fetchData = async () => {
            const result = await axios({
                method: "Get",
                url: "/movies/5edfc4110e4b720d4466ed63"
            })
            setData(result.data)
        }
    })

    useEffect(() => {
        axios({
            method: "Get",
            url: "/movies/5edfc4110e4b720d4466ed63"
        })
        .then(result => {
            const path = result.data[0]
            setTitle(path.Title)
            setPoster(path.Poster)
            setYear(path.Year)
            setSynopsis(path.Synopsis)
            setRuntime(path.Runtime)
            setRating(path.Rating)
            setDirector(path.Director)
            setGenre(path.Genre)
        })
      }, [])

    return(
        <div>
            <Navbar />
            <Sidebar input={sidebar_options}/>
            <Singleview  title={title} poster={poster} year={year} synopsis={synopsis} runtime={runtime} rating={rating} director={director} genre={genre}/>
        </div>
    )
}

export default SingleView
