import React from 'react'
import { AiOutlineSearch } from 'react-icons/ai'
import axios from 'axios'

const NavSearch = (props) => {

    const handleSubmit = (e) => {
        e.preventDefault()
        if (props.searchTerm){
            props.handleSearching()
            const identifier = props.searchTerm.slice(0,3)
            if(identifier === 'id:'){
                var id = props.searchTerm.slice(3)
                console.log('ID: ' + id)
                //Call search function with _id
                //Using Axios Post Method --> Check size of result or use json response
                var temp_term = "http://localhost:5000/movies/id/" + id
                axios({
                    method: "Get",
                    url: temp_term
                })
                .then(response => {
                    console.log(response)
                })
                .catch(error => {
                    console.log(error)
                })
            }else{
                console.log('Term: ' + props.searchTerm)
                //Call search function with searchTerm
                //Using Axios Post Method --> Check size of result or use json response
                var temp_term = "http://localhost:5000/movies/title/" + props.searchTerm
                axios({
                    method: "Get",
                    url: temp_term
                })
                .then(response => {
                    console.log(response)
                    //If response has data, redirect to singleview with data loaded
                })
                .catch(error => {
                    console.log(error)
                    //If resoonse has no data, present error and allow user to retry
                })
            }
        }
    }

    return(
    <React.Fragment>
        <form style={form_style}>
            <div style={left_div}>
                <input type="text" placeholder="Search" style={navSearch_style} onChange={props.handleSearchTerm}/>
            </div>
        </form>
        <div style={right_div}>
            <AiOutlineSearch style={search_btn} onClick={handleSubmit}/>
        </div>
    </React.Fragment>
    );
}

const form_style = {
    width: "100%",
    height: "100%"
}

const search_btn = {
    height: "4vh",
    width: "4vh",
    marginTop: "2vh",
    marginLeft: "1vw",
    color: "white"
}

const left_div = {
    flexGrow: "1",
    height: "100%",
    display: "flex"
}

const right_div = {
    width: "50px",
    height: "100%"
}

const navSearch_style = {
    height: "4.5vh",
    flexGrow: "1",
    marginTop: "1.5vh",
    borderRadius: "10px",
    fontSize: "2vh"
}

export default NavSearch
