import React, { useEffect } from 'react'
import Navbar from '../components/navbar/navbar'
import Sidebar from '../components/sidebar/sidebar'
import UserProfile from '../components/userpage/userProfile'
import SearchBar from '../components/searchbar/searchbar'

//Logout function --> Clear local storage and redirect user to login page
const handleLogOut = (e) => {
    localStorage.clear()
    setTimeout(() => {  window.location = "/login"; }, 1000);
}

const UserPage = () => {
    const [searching, setSearching] = React.useState(false)

    const handleSearching = (e) => {
        setSearching(!searching)
    }

    return(
        <div className="single_page_background">
            {searching
                ? <React.Fragment>
                    <Navbar handleSearching={handleSearching} searching={searching}/>
                    <SearchBar handleSearching={handleSearching} />
                    <Sidebar searching={searching} blank="false"/>
                    <UserProfile searching={searching} />
                  </React.Fragment>
                : <React.Fragment>
                    <Navbar handleSearching={handleSearching} searching={searching}/>
                    <Sidebar searching={searching} blank="false"/>
                    <UserProfile searching={searching} />
                  </React.Fragment>

            }
        </div>
    );
}

export default UserPage
