    const handlecurrentStage = e => {
        if (e.target.value === 'loading'){
            setcurrentStage('loading')
        }
        else if (e.target.value === 'finished'){
            setcurrentStage('finished')
        }
        else{
            setcurrentStage('default')
        }
    }

    const handleClick = e => {

    }

    return(
        <div style={parent_test}>
            <div style={child_1}>
                <img src={photo} alt="Please Upload an Image" style={image_style}/>
            </div>
            <div style={child_2}>
                <form>
                    <button style={button_style} onClick={handleClick}>Upload</button>
                </form>
            </div>
        </div>
    );
}

