import React from 'react'

const CommentElement = () => {
    return(
    <div style={comment_div}>
        <div style={rating_section}>
            <p style={rating_text}><b>Rating</b></p>
            <div style={test4}>
                <input type="number" style={num_input_style}/>
            </div>
        </div>
        <div style={comment_section}>
            <textarea style={textarea_style}></textarea>
        </div>
        <div style={btn_section}>
            <button style={submit_btn}>Submit</button>
        </div>
    </div>
    );
}

const test4 = {
    height: "130px",
    flexGrow: "1"
}

const submit_btn = {
    padding: "10px",
