    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center"
}

const comment_section = {
    flexGrow: "7",
    height: "100%"
}
const comment_div = {
    height: "150px",
    width: "100%",
    backgroundColor: "#EEEEEE",
    marginTop: "20px",
    borderRadius: "20px",
    display: "flex",
    flexDirection: "row"
}
export default CommentElement
