import React, { useState, Fragment } from 'react'

const MoviePoster = (props) => {
    const [isBlurred, setIsBlurred] = useState(false);
    return(
        <div 
         className={props.class}
         onMouseEnter={() => setIsBlurred(true)}
         onMouseLeave={() => setIsBlurred(false)}
         >
            {isBlurred ? (
                <Fragment>
                    <img 
                    src={props.poster_picture} 
                    style={props.style}
                    alt="Movie Poster"
                    />
                    <p style={text_test}>{props.title}</p>
                </Fragment>
            ): (
                <img 
                src={props.poster_picture} 
                style={test}
                alt="Movie Poster"
                />
            )}
        </div>
    );
}

const test = {
    height: "100%",
    width: '100%'
}
const text_test = {
    position: "relative",
    top: "-60%",
    fontSize: "20px",
    textAlign: "center",
    maxWidth: "225px",
    color: "white"
}

const test_blurred = {
    height: "100%",
    width: "100%",
    opacity: "0.3"
}
export default MoviePoster
