import React, { useEffect } from 'react'
import Navbar from '../components/navbar/navbar'
import Sidebar from '../components/sidebar/sidebar'

//Logout function --> Clear local storage and redirect user to login page
const handleLogOut = (e) => {
    localStorage.clear()
    setTimeout(() => {  window.location = "/login"; }, 1000);
}

const UserPage = () => {
    const [links, setLinks] = React.useState()

    const checkPrivileges = () => {
        const accessLevel = localStorage.getItem("accessLevel")
        if(accessLevel == 'admin'){
            var sidebar_links_admin = [
                {
                    index: 1,
                    heading: "Add New Movie",
                    title: "Add Movie",
                    link: "/createMovie"
                },
                {
                    index: 2,
                    heading: "View Multiview",
                    title: "Multiview",
                    link: "/multiview"
                },
                {
                    index: 3,
                    heading: "View Listview",
                    title: "Listview",
                    link: "/listview"
                },
                {
                    index: 4,
                    heading: "View Singleview",
                    title: "Singleview",
                    link: "/singleview"
                }
            ]
            setLinks(sidebar_links_admin)
        }
        if(accessLevel == 'user'){
            var sidebar_links_user = [
                {
                    index: 1,
                    heading: "View Multiview",
                    title: "Multiview",
                    link: "/multiview"
                },
                {
                    index: 2,  
                    heading: "View Listview",
                    title: "Listview",
                    link: "/listview"
                },
                {
                    index: 3,
                    heading: "View Singleview",
                    title: "Singleview",
                    link: "/singleview"
                }
            ]
            setLinks(sidebar_links_user)
        }

    }

    checkPrivileges()

    useEffect(() => {
        checkPrivileges()
        console.log('links' + links)
    }, [links])

    return(
        <div className="single_page_background">
            <Navbar />
            <Sidebar input={links} blank="false"/>
            <div style={page}>
                <div style={card}>
                    <p style={title}>Welcome to the Movie Database</p>
                    <p style={row}>Your Username Is:</p>
                    <p style={row}>{localStorage.getItem("username")}</p>
                    <p style={row}>Your User ID Is:</p>
                    <p style={row}>{localStorage.getItem("userId")}</p>
                    <p style={row}>Logout</p>
                    <button className="logout_btn" onClick={handleLogOut}>Logout</button>
                </div>
            </div>
        </div>
    );
}

const row = {
    flexGrow: "2",
    minHeight: "40px",
    fontSize: "15pt"
}

const title = {
    fontSize: "35pt",
    fontWeight: "bold",
    flexGrow: "4",
    textAlign: "center"
}

const card = {
    width: "800px",
    height: "700px",
    backgroundColor: "#EEEEEE",
    borderRadius: "20px",
    display: "flex",
    flexDirection: "column",
    padding: "0 75px 0 75px"
}

const page = {
    width: "85%",
    height: "100%",
    float: "right",
    padding: "25px"
}

export default UserPage
