import React from 'react'

const LoginPage = () => {
    const [username, setUsername] = React.useState('')
    const [password, setPassword] = React.useState('')


    const handleUsername = e => {
        setUsername(e.target.value)
    }

    const handlePassword = e => {
        setPassword(e.target.value)
    }

    return(
        <div className="single_page_background">
            <div style={login_div}>
                <div style={left_div}>

                </div>
                <div style={right_div}>
                    <div style={title_row}>
                        <p style={login_title}>Movie Database</p>
                    </div>
                    <div style={middle_row}>
                        <p style={middle_text}>Welcome to the Movie database</p>
                        <p style={middle_text}>Please Sign In:</p>
                    </div>
                    <div style={small_buffer}></div>
                    <form>
                        <div style={row}>
                            <input type="text" placeholder="username" value={username} style={login_input} onChange={handleUsername}/>
                        </div>
                        <div style={row}>
                            <input type="password" placeholder="password" value={password} style={login_input} onChange={handlePassword}/>
                        </div>
                    </form>
                    <div style={form_btns}>
                        <button>Reset</button>
                        <button>Login</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

const form_btns = {
    height: "10vh",
    width: "100%",
    display: "flex",
    justifyContent: "center"
}

const small_buffer = {
    height: "5vh"
}

const login_section = {
    width: "100%",
    height: "20vh",
}

const login_div = {
    width: "80%",
    height: "80%",
    backgroundColor: "#EEEEEE",
    position: "fixed",
    zIndex: "2",
    margin: "5% 10% 5% 10%",
    borderRadius: "10px"
}

const left_div = {
    width: "50%",
    float: "left"
}

const right_div = {
    width: "50%",
    float: "right"
}

const login_input = {
    width: "80%",
    marginLeft: "10%",
    height: "5vh",
    fontSize: "3vh"
}

const row = {
    width: "100%",
    height: "8vh",
    display: "flex",
    flexDirection: "row",
    justifyContent: "center"
}

const title_row = {
    width: "100%",
    height: "25vh",
    display: "flex",
    flexDirection: "row",
    justifyContent: "center"
}

const login_title = {
    fontSize: "6vh",
    fontWeight: "bold",
    width: "80%",
    marginLeft: "10%",
    textAlign: "center"
}

const middle_row = {
    width: "100%",
    height: "10vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center"
}

const middle_text = {
    fontSize: "2vh",
    lineHeight: "2vh",
    textAlign: "center",
    margin: "0 0 10px 0"
}
export default LoginPage
