import React from 'react'
import { AiFillStar } from 'react-icons/ai'
import MoviePoster from '../multiview/movieposter/movieposter';


const poster_style = {
    height: "175px",
    width: "110px"
}

const ListviewElement = (props) => {
    return(
        <div className="list_element">
            <div className="listview_row">
                <div style={left_area}>
                    <MoviePoster poster_picture={props.picture} inputs={poster_style} title={props.input.title} class="listview_element"/>
                </div>
                <div style={center_area}>
                    <div style={small_buffer}></div>
                    <p style={title_text}>{props.input.title}</p>
                </div>
                <div style={right_area}>
                    <div style={star_div}>
                    <div style={med_buffer}></div>
                        <AiFillStar style={rating_star}/>
                    </div>
                    <div style={rating_div}>
                    <div style={med_buffer}></div>
                        <p style={rating_text}>{props.input.rating}</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

const left_area = {
    width: "175px",
    height: "155px",
    float: "left"
}

const center_area = {
    width: "65%",
    height: "155px",
    float: "left"
}

const right_area = {
    width: "20%",
    height: "155px",
    display: "flex",
    flexDirection: "row"
}

const small_buffer = {
    height: "25px"
}

const med_buffer = {
    height: "50px"
}

const title_text = {
    fontSize: "35pt",
    margin: 0,
    lineHeight: "100px"
}

const rating_text = {
    fontSize: "30pt",
    lineHeight: "50px",
    margin: 0
}

const rating_star = {
    height: "50px",
    width: "50px"
}

const star_div = {
    width: "50px",
    height: "50px",
    flexGrow: 1,
    alignItems: "center",
    justifyContent: "center"
}

const rating_div = {
    width: "100px",
    height: "50px",
    flexGrow: 5,
    alignItems: "center",
    justifyContent: "center"
}

export default ListviewElement
