import React from 'react'
import { Link } from 'react-router-dom'
import babel from 'babel-core'

let block_len = 0

const Sidebar = (props) => {

    const linkBuilder = (props) => {
        if(props.input.blank === true){
            var output = "<React.Fragment></React.Fragment>";
        }else{
            var output = "<React.Fragment>";
            const path = props.input.links
            console.log(path)
            const num_links =Object.keys(path).length
            for (var i = 1; i < num_links; i++){
                const param = ".link" + i;
                const temp_link = props.input.links + param;
                const temp_link_title = temp_link + ".title";
                const temp_link_link = temp_link + ".link";
                const temp_link_heading = temp_link + ".heading"
                const line1_out = "<h1 style={test}>{" + temp_link_title + "}</h1>"
                const line2_out = "<Link to={" + temp_link_link + "} style={link_style}>" + temp_link_heading + "</Link>"
                output = output + line1_out + line2_out
            }
            output = output + "</React.Fragment>"
        }
        return output
    }

    var input = linkBuilder(props)
    input = eval(babel.transform(input).code)
    console.log(input)


    if(props.input.blank === false){

    }
    const block = props.input.blocks
    try {
        block_len = Object.keys(block).length
    } catch (error) {
        console.log(error)
    }

    return(
        <div className="sidebar_div">
            {input}
        </div>
    );
}

const test = {
    color: "white",
    textAlign: "center"
}

const link_style = {
    fontSize: "20pt",
    fontWeight: "bold",
    marginLeft: "10%"
}

export default Sidebar
