import photo from '../multiview/posters/12_Angry_men.jpg'

const PosterUpload = () => {
    const [currentStage, setcurrentStage] = React.useState('default')

    const handlecurrentStage = e => {
        if (e.target.value === 'loading'){
            setcurrentStage('loading')
        }
        else if (e.target.value === 'finished'){
            setcurrentStage('finished')
        }
        else{
            setcurrentStage('default')
        }
    }
    let dropbox;

    dropbox = document.getElementById("dropbox");
    dropbox.addEventListener("dragenter", dragenter, false);
    dropbox.addEventListener("dragover", dragover, false);
    dropbox.addEventListener("drop", drop, false);

    const dragenter = e => {
        e.stopPropigation();
        e.preventDefault();
    }

    const dragover = e => {
        e.stopPropigation();
        e.preventDefault();
    }

    const drop = e => {
        e.stopPropigation();
        e.preventDefault();

        const dt = e.dataTransfer;
        const file = dt.file;

        handleFile(file)
    }

    const handleFile = () => {
        console.log("You uploaded a file with drag and drop")
    }

    const handleClick = e => {

    }

    return(
        <div style={parent_test}>
            <div style={child_1} id="dropbox">
                <img src={photo} alt="Please Upload an Image" style={image_style}/>
            </div>
            <div style={child_2}>
                <form enctype="multipart/form-data">
                    <button style={button_style} onClick={handleClick}>Upload</button>
                </form>
            </div>
        </div>
    );
}

