import React, { useEffect } from 'react'
import MoviePoster from '../movieposter/movieposter';
import angry_men from '../posters/12_Angry_men.jpg'
import dark_knight from '../posters/The_Dark_Knight.jpg'
import godfather from '../posters/Godfather.jpg'
import godfather2 from '../posters/Godfather_II.jpg'
import shawshank from '../posters/Shawshank_Redemption.jpg'
import lor_ftr from '../posters/LOR_fellowship_of_the_ring.jpg'
import lor_rtk from '../posters/Lord_of_the_rings.jpg'
import tgtbth from '../posters/The_good_the_bad_and_the_ugly.jpg'
import sl from '../posters/Schindlers_List.jpg'
import pf from '../posters/Pulp_Fiction.jpg'
import axios from 'axios'

const MultiView = (props) => {


    const chunk = (array, size) => {
        const chunked_array = [];
        let index = 0;
        while (index < array.length){
            chunked_array.push(array.slice(index, size + index));
            index += size;
        }
        return chunked_array
    }
    useEffect(() => {
        const fetchData = async () => {
            const block_size = 5;
            const result = await axios("/movies/")
            const size_of = result.data.length;
            const blocks = size_of / block_size;
            const rem = size_of % block_size

            var final = []
            var x = 0
            var count = 0;
            for (x in result.data){

                for (var i = 0; i < 5; i++){
                    var temp = [];
                    temp[i] = result.data[x]
                }
                final[count] = temp
                count++
                console.log(result.data[x])
            }

            console.log(result.data)
            console.log('Total Blocks ' + blocks)
            console.log('Remainder ' + rem)
            const path = result.data[0]
        }
        fetchData();
    }, []);
    return(
        <div className="multiview_style">
            <div className="multiview_top_buffer"></div>
            <div className="multiview_area">
                <div className="multiview_top_buffer"></div>
                <div className="multiview_row">
                    <MoviePoster poster_picture={shawshank} title={props.input.item1.title} class="multiview_element"/>
                    <MoviePoster poster_picture={godfather} title={props.input.item2.title} class="multiview_element"/>
                    <MoviePoster poster_picture={godfather2} title={props.input.item3.title} class="multiview_element"/>
                    <MoviePoster poster_picture={dark_knight} title={props.input.item4.title} class="multiview_element"/>
                    <MoviePoster poster_picture={angry_men} title={props.input.item5.title} class="multiview_element"/>
                </div>
                <div className="multiview_top_buffer"></div>
                <div className="multiview_row">
                    <MoviePoster poster_picture={lor_rtk} title={props.input.item6.title} class="multiview_element"/>
                    <MoviePoster poster_picture={sl} title={props.input.item7.title} class="multiview_element"/>
                    <MoviePoster poster_picture={pf} title={props.input.item8.title} class="multiview_element"/>
                    <MoviePoster poster_picture={tgtbth} title={props.input.item9.title} class="multiview_element"/>
                    <MoviePoster poster_picture={lor_ftr} title={props.input.item10.title} class="multiview_element"/>
                </div>
            </div>
        </div>
    );
}

export default MultiView
