import React, { useEffect } from 'react'
import Navbar from '../components/navbar/navbar'
import Sidebar from '../components/sidebar/sidebar'
import UserProfile from '../components/userpage/userProfile'

//Logout function --> Clear local storage and redirect user to login page
const handleLogOut = (e) => {
    localStorage.clear()
    setTimeout(() => {  window.location = "/login"; }, 1000);
}

const UserPage = () => {
    const [searching, setSearching] = React.useState(false)

    const handleSearching = (e) => {
        setSearching(!searching)
    }

    return(
        <div className="single_page_background">
            <Navbar handleSearching={handleSearching} searching={searching}/>
            <Sidebar searching={searching} blank="false"/>
            <UserProfile searching={searching} />
        </div>
    );
}

const row = {
    flexGrow: "2",
    minHeight: "40px",
    fontSize: "15pt"
}

const title = {
    fontSize: "35pt",
    fontWeight: "bold",
    flexGrow: "4",
    textAlign: "center"
}

const card = {
    width: "800px",
    height: "700px",
    backgroundColor: "#EEEEEE",
    borderRadius: "20px",
    display: "flex",
    flexDirection: "column",
    padding: "0 75px 0 75px"
}

const page = {
    width: "85%",
    height: "100%",
    float: "right",
    padding: "25px"
}

export default UserPage
