import React from 'react'
import photo from '../multiview/posters/12_Angry_men.jpg'
import 'react-dropzone-uploader/dist/styles.css'
import Dropzone from 'react-dropzone-uploader'

const PosterUpload = () => {
    const [file, setFile] = React.useState(null)

    const fileSelectedHandler = e => {

        setFile(URL.createObjectURL(e.target.files[0]))
    }

    const handleClick = e => {
        setFile(e.target.files[0])
    }

    return(
        <div style={parent_test}>
            <div style={child_1}>
                <img src={file} alt="Testing" style={testk}/>
            </div>
            <div style={child_2}>
                <input type="file" onChange={fileSelectedHandler} id="unique_input"/>
                <label for="unique_input">Upload Poster</label>
            </div>
        </div>
    );
}

