import React, { useEffect } from 'react'
import axios from 'axios'

const CommentElement = (props) => {
    const [movieId, setMovieId] = React.useState('5edff12e85e7131563be5f3d')
    const [userId, setUserId] = React.useState(localStorage.getItem('userId'))
    const [userRating, setUserRating] = React.useState('')
    const [userComment, setUserComment] = React.useState('')
    const [commentDate, setCommentDate] = React.useState('')

    const handleRating = (e) => {
        setUserRating(e.target.value)
    }

    const handleComment = (e) => {
        setUserComment(e.target.value)
    }

    useEffect(() => {
        const endpoint = "http://localhost:5000/comments/movie/" + movieId
        const fetchData = async () => {
            const result = await axios(endpoint)
            const path = result.data
            //Parse this data into comments and display on page
            setUserRating(result.data[0].rating)
            setUserComment(result.data[0].comment)
            setCommentDate(result.data[0].date)
            console.log(path)
        }
        fetchData();
    }, [movieId]);

    return(
    <div style={comment_div}>
        <div style={rating_section}>
            <div style={heading_section}>
                <p style={rating_text}>User</p>
            </div>
            <div style={num_in_section}>
                <p style={rating_text}>{userId}</p>
            </div>
        </div>
        <div style={rating_section}>
            <div style={heading_section}>
                <p style={rating_text}>Rating</p>
            </div>
            <div style={num_in_section}>
                <p style={rating_text}>{userRating}</p>
            </div>
        </div>
        <div style={rating_section}>
            <div style={heading_section}>
                <p style={rating_text}>Date</p>
            </div>
            <div style={num_in_section}>
                <p style={rating_text}>{commentDate}</p>
            </div>
        </div>
        <div style={comment_section}>
            <div style={heading_section}>
                <p style={rating_text}>Comment</p>
            </div>
            <div style={num_in_section}>
                <p style={rating_text}>{userComment}</p>
            </div>
        </div>
    </div>
    );
}

const heading_section = {
    minHeight: "30px",
    flexGrow: "1"
}

const num_in_section = {
    height: "130px",
    flexGrow: "1"
}

const submit_btn = {
    padding: "10px",
    fontSize: "25pt",
    borderRadius: "5px"
}

const btn_section = {
    flexGrow: "1",
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
}

const rating_text = {
    textAlign: "center",
    fontSize: "12pt",
    marginRight: "10px",
    margin: 0,
    lineHeight: "30px",
    fontWeight: "bold"
}

const num_input_style = {
    width: "75px",
    height: "50px",
    fontSize: "20pt",
    textAlign: "center"
}

const textarea_style = {
    width: "90%",
    height: "50px",
    marginLeft: "5%"
}

const rating_section = {
    flexGrow: "2",
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center"
}

const comment_section = {
    flexGrow: "7",
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center"
}
const comment_div = {
    height: "100px",
    width: "100%",
    backgroundColor: "#EEEEEE",
    marginTop: "20px",
    borderRadius: "20px",
    display: "flex",
    flexDirection: "row"
}
export default CommentElement
