import React from 'react'
import { AiOutlineSearch } from 'react-icons/ai'

const NavSearch = () => {
    const [searchTerm, setSearchTerm] = React.useState('')

    const handleSearch = (e) => {
        setSearchTerm(e.target.value)
    }

    return(
    <React.Fragment>
        <form>
            <div style={left_div}>
                <input type="text" placeholder="Search" style={navSearch_style} onChange={handleSearch}/>
            </div>
        </form>
        <div style={right_div}>
            <AiOutlineSearch style={search_btn}/>
        </div>
    </React.Fragment>
    );
}

const search_btn = {
    height: "4vh",
    width: "4vh",
    marginTop: "2vh",
    marginLeft: "1vw",
    color: "white"
}

const left_div = {
    flexGrow: "1",
    height: "100%",
    display: "flex"
}

const right_div = {
    width: "50px",
    height: "100%"
}

const navSearch_style = {
    height: "4.5vh",
    width: "100%",
    marginTop: "1.5vh",
    borderRadius: "10px",
    fontSize: "2vh"
}

export default NavSearch
