import React from 'react'
import SidebarElement from './sidebarElement'

const Sidebar = (props) => {
    const [links, setLinks] = React.useState('')
    const [accessLevel, setAccessLevel] = React.useState('admin')

    console.log('access level' + accessLevel)

    setLinks([
        {
            index: 1,
            heading: "Add New Movie",
            title: "Add Movie",
            link: "/createMovie"
        },
        {
            index: 2,
            heading: "View Multiview",
            title: "Multiview",
            link: "/multiview"
        },
        {
            index: 3,
            heading: "View Listview",
            title: "Listview",
            link: "/listview"
        },
        {
            index: 4,
            heading: "View Singleview",
            title: "Singleview",
            link: "/singleview"
        }
    ])

    if(accessLevel == 'admin'){
        var sidebar_links_admin = [
            {
                index: 1,
                heading: "Add New Movie",
                title: "Add Movie",
                link: "/createMovie"
            },
            {
                index: 2,
                heading: "View Multiview",
                title: "Multiview",
                link: "/multiview"
            },
            {
                index: 3,
                heading: "View Listview",
                title: "Listview",
                link: "/listview"
            },
            {
                index: 4,
                heading: "View Singleview",
                title: "Singleview",
                link: "/singleview"
            }
        ]
        setLinks(sidebar_links_admin)
    }
    if(accessLevel == 'user'){
        var sidebar_links_user = [
            {
                index: 1,
                heading: "View Multiview",
                title: "Multiview",
                link: "/multiview"
            },
            {
                index: 2,  
                heading: "View Listview",
                title: "Listview",
                link: "/listview"
            },
            {
                index: 3,
                heading: "View Singleview",
                title: "Singleview",
                link: "/singleview"
            }
        ]
        setLinks(sidebar_links_user)
    }

    return(
        <div className="sidebar_div">
            {
                links.map((value, index) => {
                    return <SidebarElement input={value} />
                })
            }
        </div>
    );
}

const test = {
    color: "white",
    textAlign: "center"
}

export default Sidebar
