import React from 'react'

const LoginPage = () => {
    const [username, setUsername] = React.useState('')
    const [password, setPassword] = React.useState('')


    const handleUsername = e => {
        setUsername(e.target.value)
    }

    const handlePassword = e => {
        setPassword(e.target.value)
    }

    return(
        <div className="single_page_background">
            <div style={login_div}>
                <div style={left_div}>

                </div>
                <div style={right_div}>
                    <div style={title_row}>
                        <p style={login_title}>Movie Database</p>
                    </div>
                    <div style={middle_row}>
                        <p style={middle_text}>Welcome to the Movie database</p>
                        <p style={middle_text}>Please Sign In:</p>
                    </div>
                    <div style={input_row}>
                        <form style={form_style}>
                            <input type="text" placeholder="username" value={username} style={login_input_T} onChange={handleUsername}/>
                            <input type="password" placeholder="password" value={password} style={login_input_B} onChange={handlePassword}/>
                        </form>
                    </div>
                    <div style={lower_unit}>
                        <div style={btn_row}>
                            <button style={submit_btn}>Login</button>
                        </div>
                        <div style={line_div}>
                            <hr style={line_style}></hr>
                            <p style={lower_unit_text}>Or</p>
                            <hr style={line_style}></hr>
                        </div>
                        <div style={btn_row}>
                            <button style={submit_btn}>Signup</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

const form_style = {
    flexGrow: "1",
    display: "flex",
    flexDirection: "column"
}

const row_input = {
    flexGrow: "1"
}

const input_row = {
