import React from 'react'
import { AiFillStar } from 'react-icons/ai'
import MoviePoster from '../multiview/movieposter/movieposter';


const poster_style = {
    height: "150px",
    width: "110px"
}

const listview_element = {
    poster_style,
    backgroundColor: "black"
}

const ListviewElement = (props) => {
    return(
        <div className="list_element">
            <div style={left_area}>
                <MoviePoster poster_picture={props.picture} inputs={poster_style} title={props.title} style={listview_element}/>
            </div>
            <div style={center_area}>
                <div style={small_buffer}></div>
                <p style={title_text}>{props.title}</p>
            </div>
            <div style={right_area}>
                <div style={star_div}>
                <div style={med_buffer}></div>
                    <AiFillStar style={rating_star}/>
                </div>
                <div style={rating_div}>
                <div style={med_buffer}></div>
                    <p style={rating_text}>8.4</p>
                </div>
            </div>
        </div>
    );
}

const left_area = {
    height: "100%",
    flexGrow: "1",
    margin: "10px"
}

const center_area = {
    height: "100%",
    flexGrow: "8"
}

const right_area = {
    height: "100%",
    flexGrow: "1",
    display: "flex",
    flexDirection: "row"
}

const small_buffer = {
    height: "25px"
}

const med_buffer = {
    height: "50px"
}

const title_text = {
    fontSize: "35pt",
    margin: 0,
    lineHeight: "100px"
}

const rating_text = {
    fontSize: "30pt",
    lineHeight: "50px",
    margin: 0
}

const rating_star = {
    height: "50px",
    width: "50px"
}

const star_div = {
    width: "50px",
    height: "50px",
    flexGrow: 1,
    alignItems: "center",
    justifyContent: "center"
}

const rating_div = {
    width: "100px",
    height: "50px",
    flexGrow: 5,
    alignItems: "center",
    justifyContent: "center"
}

export default ListviewElement
