                </div>
                <div style={right_div}>
                    <div style={title_row}>
                        <p style={login_title}>Movie Database</p>
                    </div>
                    <div style={middle_row}>
                        <div style={icon_div}>
                            <FcCheckmark style={checkmark_style}/>
                        </div>
                        <div style={message_div}>
                            <p style={middle_text}>Registration Successful</p>
                        </div>
                        <hr></hr>
                    </div>
                    <div style={lower_unit}>
                        <div style={btn_row}>
                            <button style={submit_btn} onClick={handleSubmit}>Login</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

const icon_div = {
    flexGrow: "3"
}

const message_div = {
    flexGrow: "5"
}

const checkmark_style = {
