import React from 'react'
import Navbar from '../components/navbar/navbar'
import Sidebar from '../components/sidebar/sidebar'




const checkPrivileges = () => {
    const accessLevel = localStorage.getItem("accessLevel")
    if (accessLevel === 'user'){
        sidebar_options = {
            blank: false,
            links: {
                link1: {
                    title: "Multiview",
                    link: "/multiview"
                },
                link2: {
                    title: "Listview",
                    link: "/listview"
                },
                link3: {
                    title: "Singleview",
                    link: "/singleview"
                }
            }
        }
    }
    if (accessLevel === 'admin'){
        sidebar_options = {
            blank: false,
            marginTop: {
                marginTop: "2vh"
            },
            links: {
                link1: {
                    title: "Add Movie",
                    link: "/createMovie"
                },
                link2: {
                    title: "Multiview",
                    link: "/multiview"
                },
                link3: {
                    title: "Listview",
                    link: "/listview"
                },
                link4: {
                    title: "Singleview",
                    link: "/singleview"
                }
            }
        }
    }
    return sidebar_options
}

//Logout function --> Clear local storage and redirect user to login page
const handleLogOut = (e) => {
    localStorage.clear()
    setTimeout(() => {  window.location = "/login"; }, 1000);
}

const UserPage = () => {
    return(
        <div className="single_page_background">
            <Navbar />
            <Sidebar input={sidebar_options}/>
            <div style={page}>
                <div style={card}>
                    <p style={title}>Welcome to the Movie Database</p>
                    <p style={row}>Your Username Is:</p>
                    <p style={row}>{localStorage.getItem("username")}</p>
                    <p style={row}>Your User ID Is:</p>
                    <p style={row}>{localStorage.getItem("userId")}</p>
                    <p style={row}>Logout</p>
                    <button className="logout_btn" onClick={handleLogOut}>Logout</button>
                </div>
            </div>
        </div>
    );
}

const row = {
    flexGrow: "2",
    minHeight: "40px",
    fontSize: "15pt"
}

const title = {
    fontSize: "35pt",
    fontWeight: "bold",
    flexGrow: "4",
    textAlign: "center"
}

const card = {
    width: "800px",
    height: "700px",
    backgroundColor: "#EEEEEE",
    borderRadius: "20px",
    display: "flex",
    flexDirection: "column",
    padding: "0 75px 0 75px"
}

const page = {
    width: "85%",
    height: "100%",
    float: "right",
    padding: "25px"
}

export default UserPage
