import React, { useEffect } from 'react'
import SidebarElement from './sidebarElement'

const Sidebar = (props) => {
    const [adminAccess, setAdminAccess] = React.useState(true)

        const sidebar_links_admin = [
            {
                index: 1,
                heading: "Add New Movie",
                title: "Add Movie",
                link: "/createMovie"
            },
            {
                index: 2,
                heading: "View Multiview",
                title: "Multiview",
                link: "/multiview"
            },
            {
                index: 3,
                heading: "View Listview",
                title: "Listview",
                link: "/listview"
            },
            {
                index: 4,
                heading: "View Singleview",
                title: "Singleview",
                link: "/singleview"
            }
        ]

        const sidebar_links_user = [
            {
                index: 1,
                heading: "View Multiview",
                title: "Multiview",
                link: "/multiview"
            },
            {
                index: 2,  
                heading: "View Listview",
                title: "Listview",
                link: "/listview"
            },
            {
                index: 3,
                heading: "View Singleview",
                title: "Singleview",
                link: "/singleview"
            }
        ]


    return(
        <div className="sidebar_div">
            {adminAccess
                ? <SidebarElement input={sidebar_links_admin} />
                : <SidebarElement input={sidebar_links_user} />
            }
        </div>
    );
}

const test = {
    color: "white",
    textAlign: "center"
}

export default Sidebar
