import React from 'react'
import { AiOutlineClose } from 'react-icons/ai'

const SearchBar = (props) => {
    return(
        <div style={searchbar_style}>
            <div style={search_term_side}>
                <p>Search Term:</p>
                <p style={search_term_style}>{props.searchTerm}</p>
            </div>
            <div style={button_side}>
                <AiOutlineClose  style={close_btn_style} onClick={props.handleSearching}/>
            </div>
        </div>
    );
}

const search_term_side = {
    flexGrow: "1",
    maxWidth: "200px"
}

const button_side = {
    flexGrow: "1",
    maxWidth: "50px",
    float: "right"
}

const search_term_style = {
    color: "white",
    fontSize: "20pt",
    lineHeight: "30px"
}

const close_btn_style = {
    color: "white",
    height: "40px",
    width: "40px",
    float: "right",
    margin: "0 5px 0 160px"
}

const searchbar_style = {
    height: "200px",
    width: "100%",
    position: "absolute",
    top: "8vh",
    zIndex: 3,
    backgroundColor: "black",
    display: "flex"
}

export default SearchBar
