import React from 'react'
import { Link } from 'react-router-dom'

const Sidebar = (props) => {
    const block = props.input.blocks
    const block_len = Object.keys(block).length
    console.log(block_len)

    return(
        <div className="sidebar_div">
            {props.input.blank === false && 
            <React.Fragment>
                <div style={props.input.marginTop}></div>
                {block_len == 1 &&
                <React.Fragment>
                <h1 style={test}>{props.input.blocks.item1.title}</h1>
                <Link to={props.input.blocks.item1.link} style={link_style}>Create Movie</Link>
                </React.Fragment>
                }
                {block_len == 2 &&
                    <React.Fragment>
                     <h1 style={test}>{props.input.blocks.item1.title}</h1>
                    <Link to={props.input.blocks.item1.link} style={link_style}>Create Movie</Link>
                    <h1 style={test}>{props.input.blocks.item2.title}</h1>
                    <Link to={props.input.blocks.item2.link} style={link_style}>View Movie</Link>
                    </React.Fragment>
                }
            </React.Fragment>
            }
        </div>
    );
}

const test = {
    color: "white",
    textAlign: "center"
}

const link_style = {
    fontSize: "20pt",
    fontWeight: "bold",
    marginLeft: "10%"
}

export default Sidebar
