import React, { useEffect } from 'react'
import { AiFillStar } from 'react-icons/ai'
import axios from 'axios'
import CommentElement from '../comments/commentElement'

const SingleViewPage = (props) => {
    const [id, setId] = React.useState('')
    const [title, setTitle] = React.useState('')
    const [poster, setPoster] = React.useState('')
    const [year, setYear] = React.useState('')
    const [synopsis, setSynopsis] = React.useState('')
    const [runtime, setRuntime] = React.useState('')
    const [rating, setRating] = React.useState('')
    const [director, setDirector] = React.useState('')
    const [genre, setGenre] = React.useState('')

    useEffect(() => {
        const fetchData = async () => {
            const result = await axios("/movies/id/5ee00dfe85e7131563be5f41")
            console.log(result)
            const path = result.data
            setId(path._id)
            setTitle(path.Title)
            setPoster(path.Poster)
            setYear(path.Year)
            setSynopsis(path.Synopsis)
            setRuntime(path.Runtime)
            setRating(path.Rating)
            setDirector(path.Director)
            setGenre(path.Genre)
        }
        fetchData();
    }, []);


    return(
        <div className="singleview_style">
            <div style={test_2}>
                <div style={left_div}>
                    <img src={poster} alt="Movie Poster Missing" style={poster_style}/>
                </div>
                <div style={right_div}>
                    <div style={container}>
                        <div style={top_line}>
                            <div style={title_div}>
                                <p style={single_text}>{title}</p>
                            </div>
                            <div style={rating_div}>
                                <p style={single_text}><span><AiFillStar /></span><span>8.4</span></p>
                            </div>
                        </div>
                            <div style={row}>
                                <div style={row_inner}>
                                    <p>Year:</p>
                                    <p style={trio_text}>{year}</p>
                                </div>
                                <div style={row_inner}>
                                    <p>Runtime:</p>
                                    <p style={trio_text}>{runtime}</p>
                                </div>
                                <div style={row_inner}>
                                    <p>Rating:</p>
                                    <p style={trio_text}>{rating}</p>
                                </div>
                            </div>

                            <div style={row}>
                                <div style={row_inner}>
                                    <p>Director:</p>
                                    <p style={duo_text}>{director}</p>
                                </div>
                                <div style={row_inner}>
                                    <p>Genre:</p>
                                    <p style={duo_text}>{genre}</p>
                                </div>
                            </div>
                            <div style={row_large}>
                                <div style={single_label}>
                                    <p>Synopsis:</p>
                                </div>
                                <div style={single_desc}>
                                    <p>{synopsis}</p>
                                </div>
                            </div>
                        </div>
                    </div>
            </div>
            <CommentElement movieId={id} userId="dfssdfsdfsd"/>

        </div>
    );
}

const top_line = {
    flexGrow: "1",
