import React from 'react'

const LoginPage = () => {
    const [username, setUsername] = React.useState('')
    const [password, setPassword] = React.useState('')
    const [confirm_password, setConfirmPassword] = React.useState('')
    const [f_name, setFName] = React.useState('')
    const [l_name, setLName] = React.useState('')


    const handleUsername = e => {
        setUsername(e.target.value)
    }

    const handlePassword = e => {
        setPassword(e.target.value)
    }

    const handleConfirmPassword = e => {
        setConfirmPassword(e.target.value)
    }

    const handleFName = e => {
        setFName(e.target.value)
    }
    const handleLName = e => {
        setLName(e.target.value)
    }

    return(
        <div className="single_page_background">
            <div style={login_div}>
                <div style={left_div}>

                </div>
                <div style={right_div}>
                    <div style={title_row}>
                        <p style={login_title}>Movie Database</p>
                    </div>
                    <div style={middle_row}>
                        <p style={middle_text}>Welcome to the Movie database</p>
                        <p style={middle_text}>Signup Below:</p>
                    </div>
                    <div style={small_buffer}></div>
                    <form>
                        <div style={row}>
                            <input type="text" placeholder="First Name" value={f_name} style={login_input_2_L} onChange={handleFName}/>
                            <input type="text" placeholder="Last Name" value={l_name} style={login_input_2_R} onChange={handleLName}/>
                        </div>
                        <div style={row}>
                            <input type="text" placeholder="Username" value={username} style={login_input} onChange={handleUsername}/>
                        </div>
                        <div style={row}>
                            <input type="password" placeholder="Password" value={password} style={login_input} onChange={handlePassword}/>
                        </div>
                        <div style={row}>
                            <input type="password" placeholder="Confirm Password" value={confirm_password} style={login_input} onChange={handleConfirmPassword}/>
                        </div>
                    </form>
                    <div style={lower_unit}>
                        <div style={btn_row}>
                            <button style={submit_btn}>Signup</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

const lower_unit_text = {
    fontSize: "15pt",
    width: "10%",
    textAlign: "center"
}

const line_style = {
    width: "45%",
}

const line_div = {
    display: "flex",
    flexDirection: "row",
    alignItems: "center"

}
const lower_unit = {
    height: "40%",
    width: "100%"
}

const submit_btn = {
    flexGrow: 1,
    maxWidth: "300px",
    backgroundColor: "#27AE60",
    fontSize: "20pt",
    borderRadius: "5px"
}

const btn_row = {
    marginTop: "1vh",
    height: "7vh",
    display: "flex",
    flexDirection: "row",
    justifyContent: "center"
}

const small_buffer = {
    height: "5vh"
}

const login_div = {
    width: "80%",
    height: "80%",
    backgroundColor: "#EEEEEE",
    position: "fixed",
    zIndex: "2",
    margin: "5% 10% 5% 10%",
    borderRadius: "10px"
}

const left_div = {
    width: "50%",
    height: "100%",
    float: "left",
    backgroundColor: "#D65A31"
}

const right_div = {
    width: "50%",
    height: "100%",
    float: "right"
}

const login_input = {
    width: "60%",
    marginLeft: "10%",
    height: "5vh",
    fontSize: "3vh"
}

const row = {
    width: "100%",
    height: "8vh",
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
}

const title_row = {
    width: "100%",
    height: "15vh",
    display: "flex",
    flexDirection: "row",
    justifyContent: "center"
}

const login_title = {
    fontSize: "6vh",
    fontWeight: "bold",
    width: "80%",
    marginLeft: "10%",
    textAlign: "center"
}

const middle_row = {
    width: "100%",
    height: "10vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center"
}

const middle_text = {
    fontSize: "2vh",
    lineHeight: "2vh",
    textAlign: "center",
    margin: "0 0 10px 0"
}

const login_input_2_L = {
    flexGrow: "1",
    maxWidth: "200px",
    margin: "0 2% 0 2%",
    height: "5vh",
    fontSize: "3vh"
}

const login_input_2_R = {
    flexGrow: "1",
    maxWidth: "200px",
    margin: "0 2% 0 2%",
    height: "5vh",
    fontSize: "3vh"
}

export default LoginPage
