import React from 'react';
import { FaFileUpload } from 'react-icons/fa'

const Dropzone = (props) => {
    const [dragging, setDragging] = React.useState(false);

    const dropRef = React.createRef();

    const handleDrag = (e) => {
        e.preventDefault()
        e.stopPropagation()
    }

    const handleDragIn = (e) => {
        e.preventDefault()
        e.stopPropagation()
        if (e.dataTransfer.items && e.dataTransfer.items.length > 0) {
            setDragging(true)
          }
    }

    const handleDragOut = (e) => {
        e.preventDefault()
        e.stopPropagation()
        setDragging(false)
    }

    const handleDrop = (e) => {
        e.preventDefault()
        e.stopPropagation()
        setDragging(false)
        if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
          props.handleDrop(e.dataTransfer.files)
          e.dataTransfer.clearData()
        }
    }

    return (
        <div style={div_style}>
            {dragging
            ? <React.Fragment>
                <p style={upload_text}>Drop Here!</p>
            </React.Fragment>
            : <React.Fragment>
                <div style={off}>
                    <div style={upload_text_div}>
                        <p style={upload_text}>Drag Files to Upload</p>
                        <FaFileUpload />
                    </div>
                </div>
            </React.Fragment>
            }
        </div>
      )

}

const upload_text = {
    fontSize: "35pt"
}

const upload_text_div = {
    width: "100%",
    height: "80%",
    marginTop: "10%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column"
}

const div_style = {
    width: "100%",
    height: "100%"
}

const on = {
    width: "100%",
    height: "100%",
    border: 'dashed grey 4px',
    backgroundColor: 'rgba(255,255,255,.8)',
    opacity: "0.3"
}

const off = {
    width: "100%",
    height: "100%",
    fontSize: "36pt",
    textAlign: "center"
}

export default Dropzone
