import React from 'react';
import { FaFileUpload } from 'react-icons/fa'

class Dropzone extends React.Component {

    state = {
        drag: false,
    }

    dropRef = React.createRef()

    handleDrag = e => {
        e.preventDefault()
        e.stopPropagation() 
    }

    handleDragIn = e => {
        e.preventDefault()
        e.stopPropagation()
    }

    handleDragOut = e => {
        e.preventDefault()
        e.stopPropagation()
    }

    //Display thumbnail photo upon upload
    handleFiles = (files) => {
        console.log(files)
    }

    //Handle Drop of photo into Poster Upload Section
    handleDrop = e => {
        e.preventDefault()
        e.stopPropagation()

        const dt = e.dataTransfer;
        const file = dt.files;

        this.handleFiles(file);
    }

    componentDidMount() {
        let div = this.dropRef.current
        div.addEventListener('dragenter', this.handleDragIn)
        div.addEventListener('dragleave', this.handleDragOut)
        div.addEventListener('dragover', this.handleDrag)
        div.addEventListener('drop', this.handleDrop)
    }

    componentWillUnmount() {
        let div = this.dropRef.current
        div.removeEventListener('dragenter', this.handleDragIn)
        div.removeEventListener('dragleave', this.handleDragOut)
        div.removeEventListener('dragover', this.handleDrag)
        div.removeEventListener('drop', this.handleDrop)
      }

      render() {
        return (
          <div style={div_style} ref={this.dropRef}>
            {this.state.drag
                ? <React.Fragment>
                    <div style={on}>
                    </div>
                  </React.Fragment>
                : <React.Fragment>
                    <div style={off}>
                        <div style={upload_text_div}>
                            <p style={upload_text}>Drag Files to Upload</p>
                            <FaFileUpload />
                        </div>
                    </div>
                  </React.Fragment>
            }
            {this.props.children}
          </div>
        )
      }
}

const upload_text = {
    fontSize: "35pt"
}

const upload_text_div = {
    width: "100%",
    height: "80%",
    marginTop: "10%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column"
}

const div_style = {
    width: "100%",
    height: "100%"
}

const on = {
    width: "100%",
    height: "100%",
    border: 'dashed grey 4px',
    backgroundColor: 'rgba(255,255,255,.8)',
    opacity: "0.3"
}

const off = {
    width: "100%",
    height: "100%",
    fontSize: "36pt",
    textAlign: "center"
  }

export default Dropzone
